Circle=Backbone.Model.extend({defaults:function(){this.set(this.randomize())},initialize:function(){console.log("Hey I was born! Thanks!")},randomize:function(){var a=Math.floor(200*Math.random()+10)+"px";return{background:"#"+Math.floor(16777215*Math.random()).toString(16),width:a,height:a,top:Math.floor(900*Math.random()+10),left:Math.floor(900*Math.random()+10),transform:"rotate("+Math.floor(360*Math.random())+"deg)",transitionDuration:2e3*Math.random()+1e3+"ms"}}}),CircleCollection=Backbone.Collection.extend({model:Circle,initialize:function(){console.log("A collection was made! Rad!")}}),CircleView=Backbone.View.extend({className:"circle",events:{click:"destroy",mouseover:"scatter"},scatter:function(){this.model.set(this.model.randomize()),this.render()},initialize:function(){$(".container").append(this.el),this.render();var a=this;setInterval(function(){a.model.set(a.model.randomize()),a.render()},1e4)},render:function(){this.$el.css(this.model.attributes)},destroy:function(){this.model.destroy(),this.remove()}});var circles=new CircleCollection;circles.on("add",function(a){new CircleView({model:a})}),circles.on("remove",function(a,b){$("h2").text(b.length+" circles remaing")});for(var i=0;100>i;i++)circles.add({});